import { ref, watch, onMounted, onUnmounted } from 'vue';
export default {
  __name: 'Notification',
  props: {
    message: {
      type: String,
      default: ''
    },
    type: {
      type: String,
      default: 'success' // 'success', 'error', 'black'
    },
    duration: {
      type: Number,
      default: 2000 // milisegundos
    }
  },
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const props = __props;
    const visible = ref(false);
    let timer;
    watch(() => props.message, newMessage => {
      if (newMessage) {
        visible.value = true;
        clearTimeout(timer);
        timer = setTimeout(() => {
          visible.value = false;
        }, props.duration);
      }
    }, {
      immediate: true
    } // üëà Esto es clave
    );
    onUnmounted(() => {
      clearTimeout(timer);
    });
    const __returned__ = {
      props,
      visible,
      get timer() {
        return timer;
      },
      set timer(v) {
        timer = v;
      },
      ref,
      watch,
      onMounted,
      onUnmounted
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
