import { onMounted, ref, computed } from 'vue';
import { useRoute } from 'vue-router';

// Emitimos notificación hacia el padre (HomeView)

export default {
  __name: 'MovieDetail',
  emits: ['add-favorite'],
  setup(__props, {
    expose: __expose,
    emit: __emit
  }) {
    __expose();
    const emit = __emit;
    const route = useRoute();
    const movie = ref({});
    const isFavorite = ref(false);
    onMounted(async () => {
      const id = route.params.id;
      const response = await fetch(`https://api.themoviedb.org/3/movie/${id}?api_key=9c9d0afa2300c644bc0efbf0f1e3701a&language=es`);
      movie.value = await response.json();
      const stored = JSON.parse(localStorage.getItem('favorites') || '[]');
      isFavorite.value = stored.some(m => m.id === movie.value.id);
    });
    const addToFavorites = () => {
      const stored = JSON.parse(localStorage.getItem('favorites') || '[]');
      if (!stored.find(m => m.id === movie.value.id)) {
        stored.push(movie.value);
        localStorage.setItem('favorites', JSON.stringify(stored));
        isFavorite.value = true; // ← cambiamos estado para ocultar botón
        emit('add-favorite', {
          message: 'Película agregada a favoritos',
          type: 'success'
        });
      } else {
        emit('add-favorite', {
          message: 'Ya está en favoritos',
          type: 'error'
        });
      }
    };
    //
    const handleFavoriteNotification = payload => {
      notification.value.message = '';
      setTimeout(() => {
        notification.value = {
          message: payload.message,
          type: payload.type
        };
      }, 50);
    };
    const __returned__ = {
      emit,
      route,
      movie,
      isFavorite,
      addToFavorites,
      handleFavoriteNotification,
      onMounted,
      ref,
      computed,
      get useRoute() {
        return useRoute;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
