import { ref, onMounted } from 'vue';
import Notification from '../components/Notification.vue';
import { useRoute } from 'vue-router';
export default {
  __name: 'MovieDetail',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const route = useRoute();
    const movie = ref({});
    const isFavorite = ref(false);
    const notification = ref({
      message: '',
      type: 'success'
    });
    onMounted(async () => {
      const id = route.params.id;
      const response = await fetch(`https://api.themoviedb.org/3/movie/${id}?api_key=9c9d0afa2300c644bc0efbf0f1e3701a&language=es`);
      movie.value = await response.json();
      const stored = JSON.parse(localStorage.getItem('favorites') || '[]');
      isFavorite.value = stored.some(m => m.id === movie.value.id);
    });
    const addToFavorites = () => {
      const stored = JSON.parse(localStorage.getItem('favorites') || '[]');
      const exists = stored.find(m => m.id === movie.value.id);
      if (!exists) {
        stored.push(movie.value);
        localStorage.setItem('favorites', JSON.stringify(stored));
        isFavorite.value = true;
        notification.value = {
          message: 'Película agregada a favoritos',
          type: 'success'
        };
      } else {
        notification.value = {
          message: 'Ya está en favoritos',
          type: 'error'
        };
      }
    };
    const __returned__ = {
      route,
      movie,
      isFavorite,
      notification,
      addToFavorites,
      ref,
      onMounted,
      Notification,
      get useRoute() {
        return useRoute;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
